fn node_{{id}}(h: f32, s: f32, v: f32) -> vec3f {
  let c = v * s;
  let x = c * (1.0 - abs(((h * 6.0) % 2.0) - 1.0));
  let m = v - c;
  var rgb: vec3f;
  let hp = h * 6.0;
  if (hp < 1.0) { rgb = vec3f(c, x, 0.0); }
  else if (hp < 2.0) { rgb = vec3f(x, c, 0.0); }
  else if (hp < 3.0) { rgb = vec3f(0.0, c, x); }
  else if (hp < 4.0) { rgb = vec3f(0.0, x, c); }
  else if (hp < 5.0) { rgb = vec3f(x, 0.0, c); }
  else { rgb = vec3f(c, 0.0, x); }
  return rgb + vec3f(m); 
}

fn node_{{id}}_r(c: vec3f) -> f32 { return c.r; }
fn node_{{id}}_g(c: vec3f) -> f32 { return c.g; }
fn node_{{id}}_b(c: vec3f) -> f32 { return c.b; }
